// Filter alerts by coach email and optional filters from query params
const coachEmail = $('GET /alerts').first().json.query?.coachEmail?.toLowerCase() || '';
const alertType = $('GET /alerts').first().json.query?.alertType || ''; // RED, ORANGE, or empty for all
const daysBack = parseInt($('GET /alerts').first().json.query?.daysBack) || 7;

const allAlerts = $input.all();
const cutoffDate = new Date();
cutoffDate.setDate(cutoffDate.getDate() - daysBack);

const filteredAlerts = allAlerts.filter(item => {
  const email = (item.json['Coach Email'] || '').toLowerCase();
  const type = item.json['Alert Type'] || '';
  const createdAt = new Date(item.json['Created At'] || 0);

  // Filter by coach email if provided
  if (coachEmail && email !== coachEmail) return false;

  // Filter by alert type if provided
  if (alertType && type !== alertType) return false;

  // Filter by date range
  if (createdAt < cutoffDate) return false;

  return true;
});

// Transform to expected format
const alerts = filteredAlerts.map(item => ({
  'Alert ID': item.json['Alert ID'],
  'Alert Type': item.json['Alert Type'],
  'Trainee Email': item.json['Trainee Email'],
  'Trainee Name': item.json['Trainee Name'],
  'Coach Email': item.json['Coach Email'],
  'Coach Name': item.json['Coach Name'],
  'Issue': item.json['Issue'],
  'Days Behind': item.json['Days Behind'],
  'Failed Assessments': item.json['Failed Assessments'],
  'Status': item.json['Status'] || 'Active',
  'Created At': item.json['Created At'],
  'Resolved At': item.json['Resolved At'] || null
}));

// Sort by created date, most recent first
alerts.sort((a, b) => {
  const dateA = new Date(a['Created At'] || 0).getTime();
  const dateB = new Date(b['Created At'] || 0).getTime();
  return dateB - dateA;
});

return [{ json: { alerts, count: alerts.length } }];
