// Filter scores by trainee email from query parameter
const traineeEmail = $('GET /scores').first().json.query?.traineeEmail?.toLowerCase() || '';

if (!traineeEmail) {
  return [{ json: { error: 'traineeEmail query parameter is required', scores: [] } }];
}

const allScores = $input.all();
const filteredScores = allScores.filter(item => {
  const email = (item.json['Trainee Email'] || '').toLowerCase();
  return email === traineeEmail;
});

// Transform to expected format
const scores = filteredScores.map(item => ({
  'Trainee Email': item.json['Trainee Email'],
  'Trainee Name': item.json['Trainee Name'],
  'Assessment Name': item.json['Assessment Name'],
  'Score': item.json['Score'],
  'Passed': item.json['Passed'],
  'Attempt Number': item.json['Attempt Number'] || 1,
  'Submitted At': item.json['Submitted At'] || item.json['Timestamp'],
  'Coach Email': item.json['Coach Email']
}));

// Sort by date, most recent first
scores.sort((a, b) => {
  const dateA = new Date(a['Submitted At'] || 0).getTime();
  const dateB = new Date(b['Submitted At'] || 0).getTime();
  return dateB - dateA;
});

return [{ json: { scores } }];
