// ===== WIN 3: COMBINED PACE + FAILURES ALERT SYSTEM =====
// HYBRID MODE: Log all alerts, email only RED alerts

const allItems = $input.all();

// Categorize data
const trainees = [];
const scores = [];
const schedule = [];
const coaches = [];
const alertLog = [];

allItems.forEach(item => {
  const data = item.json;

  if (data['Training Start Date'] !== undefined && data['Email Address']) {
    trainees.push(data);
  }
  else if (data['Score ID']) {
    scores.push(data);
  }
  else if (data['Schedule Day'] !== undefined) {
    schedule.push(data);
  }
  else if (data['Coach Email'] && data['Coach Name']) {
    coaches.push(data);
  }
  else if (data['Alert ID']) {
    alertLog.push(data);
  }
});

console.log(`Trainees: ${trainees.length}, Scores: ${scores.length}, Schedule: ${schedule.length}, Coaches: ${coaches.length}, Alerts: ${alertLog.length}`);

// Build schedule by role: { role -> { day -> [assessments] } }
const scheduleByRole = new Map();
schedule.forEach(s => {
  const role = s.Role;
  const day = parseInt(s['Schedule Day']) || 0;
  const assessment = s['Assessment Name'];
  if (!role || !assessment || !day) return;

  if (!scheduleByRole.has(role)) {
    scheduleByRole.set(role, new Map());
  }
  const roleSchedule = scheduleByRole.get(role);
  if (!roleSchedule.has(day)) {
    roleSchedule.set(day, []);
  }
  roleSchedule.get(day).push(assessment);
});

// Build coach lookup
const coachMap = new Map();
coaches.forEach(c => {
  const role = c.Role || c.Department || '';
  const country = c.Country || '';
  const key = `${role}-${country}`;
  if (!coachMap.has(key)) coachMap.set(key, []);
  coachMap.get(key).push(c);
});

// Build scores by trainee
const scoresByTrainee = new Map();
scores.forEach(s => {
  const email = (s['Trainee Email'] || '').toLowerCase();
  if (!email) return;
  if (!scoresByTrainee.has(email)) {
    scoresByTrainee.set(email, []);
  }
  scoresByTrainee.get(email).push(s);
});

// Build recent alerts (last 7 days)
const recentAlerts = new Set();
const sevenDaysAgo = Date.now() - (7 * 24 * 60 * 60 * 1000);
alertLog.forEach(a => {
  const sentAt = new Date(a['Sent At']).getTime();
  if (sentAt > sevenDaysAgo) {
    const key = `${(a['Trainee Email'] || '').toLowerCase()}-${a['Alert Type']}`;
    recentAlerts.add(key);
  }
});

// Helper: Get coach emails
function getCoachEmails(role, country) {
  const key = `${role}-${country}`;
  const deptCoaches = coachMap.get(key) || [];
  return deptCoaches.map(c => c['Coach Email']).filter(Boolean).join(', ') || 'andrea.kaur@storehub.com';
}

// Helper: Calculate expected assessments by day
function getExpectedByDay(role, currentDay) {
  const roleSchedule = scheduleByRole.get(role);
  if (!roleSchedule) return [];

  const expected = [];
  roleSchedule.forEach((assessments, day) => {
    if (day <= currentDay) {
      expected.push(...assessments);
    }
  });
  return expected;
}

// Helper: Get passed assessments
function getPassedAssessments(traineeScores) {
  const passed = new Set();
  traineeScores.forEach(s => {
    if (s.Passed === 'Pass') {
      passed.add(s['Assessment Name']);
    }
  });
  return passed;
}

// Helper: Count total failures (not yet passed)
function countFailures(traineeScores) {
  const passedSet = getPassedAssessments(traineeScores);
  let failures = 0;
  const failedAssessments = [];

  // Group by assessment to count failures per assessment
  const failsByAssessment = new Map();
  traineeScores.forEach(s => {
    const name = s['Assessment Name'];
    if (s.Passed !== 'Pass' && !passedSet.has(name)) {
      if (!failsByAssessment.has(name)) {
        failsByAssessment.set(name, []);
      }
      failsByAssessment.get(name).push(s);
    }
  });

  failsByAssessment.forEach((attempts, name) => {
    failures += attempts.length;
    failedAssessments.push(`${name} (${attempts.length}x)`);
  });

  return { count: failures, details: failedAssessments };
}

// Helper: Determine pace
function determinePace(expected, passed) {
  const expectedCount = expected.length;
  const passedCount = passed.size;

  if (expectedCount === 0) return { pace: 'on-track', emoji: '‚úÖ', label: 'On Track' };

  const completionRate = passedCount / expectedCount;

  if (passedCount > expectedCount) {
    return { pace: 'ahead', emoji: 'üêá', label: 'Ahead of Schedule' };
  } else if (completionRate < 0.7) {
    return { pace: 'behind', emoji: 'üê¢', label: 'Behind Schedule' };
  } else {
    return { pace: 'on-track', emoji: '‚úÖ', label: 'On Track' };
  }
}

// Process trainees and generate alerts
const alerts = [];
const today = new Date().toISOString();

trainees.forEach(trainee => {
  const email = (trainee['Email Address'] || '').toLowerCase();
  const name = trainee['Full Name'] || trainee.Name || '';
  const role = trainee['Department/Role'] || trainee.Department || trainee.Role || '';
  const country = trainee.Country || '';
  const startDate = trainee['Training Start Date'];
  const status = trainee.Status || '';

  // Skip inactive trainees
  if (status === 'Completed' || status === 'Terminated' || status === 'On Hold') return;
  if (!email || !name || !startDate) return;

  // Calculate current training day
  const currentDay = Math.floor((Date.now() - new Date(startDate).getTime()) / (1000 * 60 * 60 * 24)) + 1;

  // Skip if just started (day 1)
  if (currentDay < 2) return;

  const traineeScores = scoresByTrainee.get(email) || [];
  const expectedAssessments = getExpectedByDay(role, currentDay);
  const passedAssessments = getPassedAssessments(traineeScores);
  const { count: failureCount, details: failureDetails } = countFailures(traineeScores);
  const { pace, emoji: paceEmoji, label: paceLabel } = determinePace(expectedAssessments, passedAssessments);

  const coachEmails = getCoachEmails(role, country);

  // ===== COMBINED ALERT MATRIX =====
  // Behind + 1+ fail ‚Üí RED (Struggling) ‚Üí EMAIL + LOG
  // Behind + 0 fail ‚Üí ORANGE (Stuck) ‚Üí LOG ONLY (dashboard)
  // On-track + 3+ fail ‚Üí RED (Knowledge gaps) ‚Üí EMAIL + LOG
  // On-track + 2 fail ‚Üí ORANGE (Needs support) ‚Üí LOG ONLY (dashboard)
  // Ahead + 2+ fail ‚Üí ORANGE (Rushing) ‚Üí LOG ONLY (dashboard)
  // Otherwise ‚Üí None

  let alertType = null;
  let alertReason = '';
  let alertEmoji = '';
  let coachingAction = '';

  if (pace === 'behind' && failureCount >= 1) {
    alertType = 'RED';
    alertEmoji = 'üî¥';
    alertReason = `Behind schedule + ${failureCount} failure(s) - Struggling learner`;
    coachingAction = 'Schedule 1:1 intensive coaching session. Review fundamentals and provide additional support.';
  }
  else if (pace === 'behind' && failureCount === 0) {
    alertType = 'ORANGE';
    alertEmoji = 'üü†';
    alertReason = `Behind schedule, no attempts - Stuck/Disengaged`;
    coachingAction = 'Check in with trainee. They may be stuck, confused, or disengaged. Motivate and clarify next steps.';
  }
  else if (pace === 'on-track' && failureCount >= 3) {
    alertType = 'RED';
    alertEmoji = 'üî¥';
    alertReason = `On track but ${failureCount} failures - Knowledge gaps`;
    coachingAction = 'Review failed assessments. Identify specific knowledge gaps and provide targeted coaching.';
  }
  else if (pace === 'on-track' && failureCount === 2) {
    alertType = 'ORANGE';
    alertEmoji = 'üü†';
    alertReason = `On track but 2 failures - Needs support`;
    coachingAction = 'Monitor closely. Offer additional resources or coaching before next attempt.';
  }
  else if (pace === 'ahead' && failureCount >= 2) {
    alertType = 'ORANGE';
    alertEmoji = 'üü†';
    alertReason = `Ahead of schedule but ${failureCount} failures - Rushing`;
    coachingAction = 'Trainee is moving fast but not absorbing content. Advise to slow down and review material properly.';
  }

  // Skip if no alert needed
  if (!alertType) return;

  // Check for duplicate
  const alertKey = `${email}-${alertType}`;
  if (recentAlerts.has(alertKey)) {
    console.log(`Skip ${alertType} (recent): ${name}`);
    return;
  }

  // Build detailed status
  const expectedCount = expectedAssessments.length;
  const passedCount = passedAssessments.size;
  const incompleteList = expectedAssessments.filter(a => !passedAssessments.has(a));

  console.log(`${alertEmoji} ${alertType}: ${name} - ${alertReason}`);

  alerts.push({
    json: {
      'Alert ID': `ALERT-${Date.now()}-${Math.random().toString(36).substr(2, 5)}`,
      'Trainee Email': email,
      'Trainee Name': name,
      'Alert Type': alertType,
      'Alert Reason': alertReason,
      'Coach Email': coachEmails,
      'Sent At': today,
      'Assessment Name': failureDetails.join(', ') || incompleteList.join(', '),
      'Failed Attempts': failureCount,
      'Notes': `Pace: ${paceLabel} | Day: ${currentDay}`,
      '_role': role,
      '_country': country,
      '_currentDay': currentDay,
      '_pace': pace,
      '_paceLabel': paceLabel,
      '_passedCount': passedCount,
      '_expectedCount': expectedCount,
      '_failureCount': failureCount,
      '_sendEmail': alertType === 'RED',
      '_subject': `${alertEmoji} ${alertType} ALERT: ${name} - ${paceLabel}`,
      '_body': `Hi Team,\n\n${alertEmoji} ${alertType} ALERT\n\n` +
        `‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ\n` +
        `TRAINEE DETAILS\n` +
        `‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ\n` +
        `Name: ${name}\n` +
        `Role: ${role} (${country})\n` +
        `Training Day: ${currentDay}\n\n` +
        `‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ\n` +
        `STATUS\n` +
        `‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ\n` +
        `Pace: ${paceEmoji} ${paceLabel}\n` +
        `Assessments Passed: ${passedCount}/${expectedCount}\n` +
        `Total Failures: ${failureCount}\n\n` +
        (failureCount > 0 ? `Failed Assessments:\n${failureDetails.map(f => `‚Ä¢ ${f}`).join('\n')}\n\n` : '') +
        (incompleteList.length > 0 ? `Incomplete Assessments:\n${incompleteList.map(a => `‚Ä¢ ${a}`).join('\n')}\n\n` : '') +
        `‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ\n` +
        `ISSUE\n` +
        `‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ\n` +
        `${alertReason}\n\n` +
        `‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ\n` +
        `RECOMMENDED ACTION\n` +
        `‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ\n` +
        `${coachingAction}\n\n` +
        `View in Learning Hub: https://training.storehub.com/dashboard\n\n` +
        `Cheers,\nTraining LMS`
    }
  });

  recentAlerts.add(alertKey);
});

console.log(`\nTotal alerts: ${alerts.length}`);
console.log(`RED (email): ${alerts.filter(a => a.json._sendEmail).length}`);
console.log(`ORANGE (dashboard only): ${alerts.filter(a => !a.json._sendEmail).length}`);

if (alerts.length === 0) {
  return [{ json: { _noAlerts: true, message: 'No alerts needed today' } }];
}

return alerts;
